import _ from 'lodash';
import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import './Piano.css';
import { Key } from './Key.js'
import {
  NOTES,
  VALID_KEYS,
  KEY_TO_NOTE,
} from '../global/constants';

class Piano extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pressedKeys: [],
    };
  }

  playNote = (note) => {
    if (!_.isEmpty(note)) {
      const noteAudio = new Audio(document.getElementById(note).src);
      noteAudio.play();
    }
  }

  handleKeyDown = (event) => {
    if (event.repeat) {
      return;
    }
    const key = event.key;
    const updatedPressedKeys = [...this.state.pressedKeys];
    if (!updatedPressedKeys.includes(key) && VALID_KEYS.includes(key)) {
      updatedPressedKeys.push(key);
    }
    this.setState({
      pressedKeys: updatedPressedKeys,
    });
    this.playNote(KEY_TO_NOTE[key]);
  }

  handleKeyUp = (event) => {
    const index = this.state.pressedKeys.indexOf(event.key);
    if (index > -1) {
      this.setState(state => ({
        pressedKeys: state.pressedKeys.splice(index, 1)
      }));
    }
  }

  componentDidMount = () => {
    window.addEventListener('keydown', this.handleKeyDown);
    window.addEventListener('keyup', this.handleKeyUp);
  }

  getnotes = async () =>
  {
    const response = await fetch('http://localhost:8080',
    {
      method: 'GET'
    })
    const notes = await response.json()
    console.long(notes)
  }

  render() {
    const keys = _.map(NOTES, (note, index) => {
      return (
        <Key
          key={index}
          note={note}
          pressedKeys={this.state.pressedKeys}
        />
      );
    });

    const audioFiles = _.map(NOTES, (note, index) => {
      return (
        <audio
          id={note}
          key={index}
          src={`../../notes/${note}.mp3`}
        />
      );
    });

    async function fetchData() {
      try {
        const response = await fetch('http://localhost:8080/');
        const data = await response.json();
    
        // Process and display the data
        data.forEach((document) => {
          document.notes.forEach((note) => {
            console.log(note); // Display each element separately (you can modify this part as needed)
          });
        });
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();

    return (
      <div>
        <h2 className = "over">Try playing the song!!</h2>
        <div className = "piano-container">
          <div className="piano">
            {keys}
          </div>
          <div>
            {audioFiles}
          </div>
        </div>
        <div className = "over">
        <Link to = "/">
          <button className = "HomeButton">Back to Home Page</button>
        </Link>
        </div>
      </div>
    );
  }
}

export default Piano;
