import React from 'react';

const Background = () => {
  const backgroundImageUrl = 'https://media.istockphoto.com/id/1076840920/vector/music-background.jpg?s=612x612&w=0&k=20&c=bMG2SEUYaurIHAjtRbw7bmjLsXyT7iJUvAM5HjL3G3I=https://img.freepik.com/premium-photo/abstract-music-notes-blurry-lights-bright-background_250469-15899.jpg';

  const containerStyle = {
    backgroundImage: backgroundImageUrl,
    backgroundSize: 'cover',
    backgroundPosition: 'center center',
    height: '100vh', // Set the height to 100% of the viewport height
    margin: 0, // Remove default margin
    padding: 0, // Remove default padding
    display: 'flex', // You can customize this based on your layout needs
    flexDirection: 'column', // You can customize this based on your layout needs
    // Add other styles as needed
  };

  return (
    <div style={containerStyle}>
    </div>
  );
};

export default Background;
